mapper.update:
update laboratory
set name          = #{name},
    buildingNo    = #{buildingNo},
    isAppointment = #{isAppointment},
    updateTime    = #{updateTime}
where id = #{id}

UPDATE user_laboratory
set returnTime  = #{returnTime},
    overdueTime = #{overdueTime},
    auditId     = #{auditId},
    state       = #{state}
where id = #{id}
